$(document).ready(function(){var n=0;$(window).scroll(function(){var t=$("#counter").offset().top-window.innerHeight;0==n&&$(window).scrollTop()>t&&($(".count").each(function(){var t=$(this),n=t.attr("data-count");$({countNum:t.text()}).animate({countNum:n},{duration:2e3,easing:"swing",step:function(){t.text(Math.floor(this.countNum))},complete:function(){t.text(this.countNum)}})}),n=1)})});